sys_variable_value.value:
(function execute(inputs, outputs) {

    var count = -1;

    if (inputs.responseStatus == "200") {
        var obj = JSON.parse(inputs.responseBody);

        count = obj.Networks.length;
    }

    outputs.count = count;
    
})(inputs, outputs);

