sys_variable_value.value:
(function execute(inputs, outputs) {

  var count = 0;
  var code = parseInt(inputs.statusCode, 10);

  if (code == 200) {
  	count = inputs.root.Playlists.length;
  }

  outputs.count = count;

})(inputs, outputs);

