sys_variable_value.value:
(function execute(inputs, outputs) {


    // Convert from GlideDateTime to T format
    var pdt = inputs.publish_date_time;
    var pdtOut = '';

    if (pdt) {
        var gdt = new GlideDateTime();
        gdt.setDisplayValue(pdt);
        pdtOut = gdt.getValue().replace(' ', 'T') + 'Z';
    }

    // Convert the playlist IDs from CSV string to array for the JSON builder
    var playlistInput = inputs.playlist_ids.toString();
    var list = playlistInput.split(',');

    var payload = {
        "Title" : inputs.title.toString(),
        "DescriptionHtml" : inputs.description.toString(),
        "Draft" : inputs.draft,
        "Visibility" : inputs.visibility.toString(),
        "PublishedUtc" : pdtOut
    };

    if (inputs.season) {
        payload.Season = inputs.season;
    }

    if (inputs.episode) {
        payload.Episode = inputs.episode;
    }

    if (list.length > 0) {
        payload.PlaylistIds = list;
    }

    if (pdtOut) {
        var schedRule = {
            "ScheduledAtUtc" : pdtOut,
            "Visibility" : "Public",
            "ResetPublishDate" : true
        };
        payload.ScheduleRules = [schedRule];
    }

    outputs.payload = JSON.stringify(payload, null, 4);
   
})(inputs, outputs);

